import React, { useState, useEffect, useRef } from 'react';
import { FaBell } from 'react-icons/fa';
import './NotificationBell.css';
import axios from 'axios';

const NotificationBell = () => {
  const [open, setOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const bellRef = useRef();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;
    axios.get('http://localhost:5000/notifications', {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => {
      setNotifications(res.data);
      setUnreadCount(res.data.filter(n => !n.is_read).length);
    });
  }, [open]);

  useEffect(() => {
    function handleClickOutside(e) {
      if (bellRef.current && !bellRef.current.contains(e.target)) setOpen(false);
    }
    if (open) document.addEventListener('mousedown', handleClickOutside);
    else document.removeEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [open]);

  return (
    <div className="notification-bell-wrapper" ref={bellRef}>
      <div className="notification-bell-icon" onClick={() => setOpen(!open)}>
        <FaBell size={22} />
        {unreadCount > 0 && <span className="notification-bell-badge">{unreadCount}</span>}
      </div>
      {open && (
        <div className="notification-bell-dropdown">
          <div className="notification-bell-title">Bildirimler</div>
          {notifications.length === 0 ? (
            <div className="notification-bell-empty">Hi√ß bildirimin yok.</div>
          ) : (
            notifications.map(n => (
              <div key={n.id} className={"notification-bell-item" + (n.is_read ? '' : ' unread')}>
                <div className="notification-bell-message">{n.message}</div>
                <div className="notification-bell-date">{new Date(n.created_at).toLocaleString('tr-TR')}</div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default NotificationBell;
