import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { parseJwt } from '../utils/jwt';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';

const RegisterPage = () => {
  const [form, setForm] = useState({ username: '', password: '', is_admin: false });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }
    const payload = parseJwt(token);
    setUsername(payload?.username || '');
    setIsAdmin(!!payload?.is_admin);
    if (!payload?.is_admin) {
      navigate('/home');
    }
  }, [navigate]);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleCheckbox = e => {
    setForm({ ...form, is_admin: e.target.checked });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError(''); setSuccess('');
    try {
      await axios.post('http://localhost:5000/register', form, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSuccess('Kayıt başarılı! Giriş sayfasına yönlendiriliyorsunuz...');
      setTimeout(() => navigate('/'), 1500);
    } catch (err) {
      setError(err.response?.data?.message || 'Kayıt başarısız!');
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: '#f4f6fa', display: 'flex' }}>
      <Sidebar username={username} isAdmin={isAdmin} />
      <div style={{ marginLeft: 200, flex: 1, minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        <div style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ maxWidth: 400, width: '100%', padding: 24, boxShadow: '0 0 10px #ccc', borderRadius: 8, background: '#fff' }}>
            <h2>Kayıt Ol</h2>
            <form onSubmit={handleSubmit}>
              <input name="username" placeholder="Kullanıcı Adı" value={form.username} onChange={handleChange} required style={{ width: '100%', marginBottom: 12, padding: 8 }} />
              <input name="password" type="password" placeholder="Şifre" value={form.password} onChange={handleChange} required style={{ width: '100%', marginBottom: 12, padding: 8 }} />
              <label style={{ display: 'block', marginBottom: 12 }}>
                <input type="checkbox" checked={form.is_admin} onChange={handleCheckbox} /> Admin olarak kaydol
              </label>
              <button type="submit" style={{ width: '100%', padding: 10 }}>Kayıt Ol</button>
              {error && <div style={{ color: 'red', marginTop: 10 }}>{error}</div>}
              {success && <div style={{ color: 'green', marginTop: 10 }}>{success}</div>}
            </form>
          </div>
        </div>
        <Footer />
      </div>
    </div>
  );
};

export default RegisterPage;
