import React, { useState, useEffect } from 'react';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';
import { parseJwt } from '../utils/jwt';
import axios from 'axios';

const ProductListPage = () => {
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const payload = parseJwt(token);
      setUsername(payload?.username || '');
      setIsAdmin(!!payload?.is_admin);
    }
  }, []);

  useEffect(() => {
    setLoading(true);
    axios.get('http://localhost:5000/products')
      .then(res => {
        setProducts(res.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Ürünler alınamadı!');
        setLoading(false);
      });
  }, []);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('tr-TR', {
      style: 'currency',
      currency: 'TRY'
    }).format(price);
  };

  const getStockStatus = (quantity, minStock) => {
    if (quantity <= 0) return { text: 'Stokta Yok', color: '#dc3545' };
    if (quantity <= minStock) return { text: 'Kritik Stok', color: '#ffc107' };
    return { text: 'Stokta', color: '#28a745' };
  };

  return (
    <div style={{ minHeight: '100vh', background: '#f4f6fa', display: 'flex' }}>
      <Sidebar username={username} isAdmin={isAdmin} />
      <div style={{ marginLeft: 200, flex: 1, minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        <div style={{ flex: 1, display: 'flex', alignItems: 'flex-start', justifyContent: 'center', paddingTop: 60 }}>
          <div style={{ width: '100%', maxWidth: 1200, background: '#fff', borderRadius: 10, boxShadow: '0 2px 12px #0001', padding: 32 }}>
            <h2 style={{ color: '#365256', marginBottom: 24 }}>Ürün Listesi</h2>
            {loading ? (
              <div>Yükleniyor...</div>
            ) : error ? (
              <div style={{ color: 'red' }}>{error}</div>
            ) : products.length === 0 ? (
              <div>Hiç ürün yok.</div>
            ) : (
              <div style={{ overflowX: 'auto' }}>
                <table style={{ width: '100%', borderCollapse: 'collapse', fontSize: 14 }}>
                  <thead>
                    <tr style={{ background: '#f7f6f3' }}>
                      <th style={{ padding: 12, textAlign: 'left', color: '#365256', borderBottom: '2px solid #365256' }}>Ürün Adı</th>
                      <th style={{ padding: 12, textAlign: 'left', color: '#365256', borderBottom: '2px solid #365256' }}>Kategori</th>
                      <th style={{ padding: 12, textAlign: 'left', color: '#365256', borderBottom: '2px solid #365256' }}>Stok</th>
                      <th style={{ padding: 12, textAlign: 'left', color: '#365256', borderBottom: '2px solid #365256' }}>Birim</th>
                      <th style={{ padding: 12, textAlign: 'left', color: '#365256', borderBottom: '2px solid #365256' }}>Fiyat</th>
                      <th style={{ padding: 12, textAlign: 'left', color: '#365256', borderBottom: '2px solid #365256' }}>SKU</th>
                      <th style={{ padding: 12, textAlign: 'left', color: '#365256', borderBottom: '2px solid #365256' }}>Tedarikçi</th>
                      <th style={{ padding: 12, textAlign: 'left', color: '#365256', borderBottom: '2px solid #365256' }}>Ekleyen</th>
                      <th style={{ padding: 12, textAlign: 'left', color: '#365256', borderBottom: '2px solid #365256' }}>Tarih</th>
                    </tr>
                  </thead>
                  <tbody>
                    {products.map(p => {
                      const stockStatus = getStockStatus(p.quantity, p.min_stock_level);
                      return (
                        <tr key={p.id} style={{ borderBottom: '1px solid #eee' }}>
                          <td style={{ padding: 12 }}>
                            <div style={{ fontWeight: 'bold', color: '#365256' }}>{p.name}</div>
                            {p.description && (
                              <div style={{ fontSize: 12, color: '#666', marginTop: 4 }}>
                                {p.description.length > 50 ? p.description.substring(0, 50) + '...' : p.description}
                              </div>
                            )}
                          </td>
                          <td style={{ padding: 12 }}>
                            {p.category || '-'}
                          </td>
                          <td style={{ padding: 12 }}>
                            <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                              <span style={{ fontWeight: 'bold' }}>{p.quantity}</span>
                              <span style={{ 
                                fontSize: 11, 
                                padding: '2px 6px', 
                                borderRadius: 12, 
                                backgroundColor: stockStatus.color + '20',
                                color: stockStatus.color,
                                fontWeight: 'bold'
                              }}>
                                {stockStatus.text}
                              </span>
                            </div>
                          </td>
                          <td style={{ padding: 12 }}>
                            {p.unit || 'adet'}
                          </td>
                          <td style={{ padding: 12, fontWeight: 'bold' }}>
                            {formatPrice(p.price)}
                          </td>
                          <td style={{ padding: 12, fontSize: 12, color: '#666' }}>
                            {p.sku || '-'}
                          </td>
                          <td style={{ padding: 12, fontSize: 12 }}>
                            {p.supplier || '-'}
                          </td>
                          <td style={{ padding: 12, fontSize: 12 }}>
                            {p.created_by_username || '-'}
                          </td>
                          <td style={{ padding: 12, fontSize: 12, color: '#666' }}>
                            {new Date(p.created_at).toLocaleDateString('tr-TR')}
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
        <Footer />
      </div>
    </div>
  );
};

export default ProductListPage;
