import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';
import { parseJwt } from '../utils/jwt';

const HomePage = ({ handleLogout }) => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [loginUser, setLoginUser] = useState('');
  const [loginTime, setLoginTime] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
    } else {
      const payload = parseJwt(token);
      setUsername(payload?.username || '');
      setIsAdmin(!!payload?.is_admin);
    }
    setLoginUser(localStorage.getItem('loginUser') || '');
    setLoginTime(localStorage.getItem('loginTime') || '');
  }, [navigate]);

  return (
    <div style={{ minHeight: '100vh', background: '#f4f6fa', display: 'flex' }}>
      <Sidebar username={username} isAdmin={isAdmin} onLogout={handleLogout} />
      <div style={{
        marginLeft: 200,
        flex: 1,
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
      }}>
        <div style={{ flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{
            background: '#f7f6f3',
            borderRadius: 10,
            padding: '18px 32px',
            marginBottom: 32,
            fontSize: 20,
            color: '#365256',
            fontWeight: 600,
            boxShadow: '0 2px 12px #0001',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'flex-start',
            gap: 8
          }}>
            <div>Giriş Yapan: <b>{loginUser}</b></div>
            <div>Giriş Saati: <b>{loginTime}</b></div>
          </div>
          <h2 style={{ fontSize: 32, color: '#222', marginBottom: 32 }}>Hoşgeldin!</h2>
          {/* Son işlemler paneli buraya eklenecek */}
        </div>
        <Footer />
      </div>
    </div>
  );
};

export default HomePage;
