import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';
import { parseJwt } from '../utils/jwt';
import axios from 'axios';

const ProductDetailPage = () => {
  const { id } = useParams();
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const payload = parseJwt(token);
      setUsername(payload?.username || '');
      setIsAdmin(!!payload?.is_admin);
    }
  }, []);

  useEffect(() => {
    setLoading(true);
    axios.get(`http://localhost:5000/products`)
      .then(res => {
        const found = res.data.find(p => String(p.id) === String(id));
        setProduct(found);
        setLoading(false);
      })
      .catch(() => {
        setError('Ürün bilgisi alınamadı!');
        setLoading(false);
      });
  }, [id]);

  return (
    <div style={{ minHeight: '100vh', background: '#f4f6fa', display: 'flex' }}>
      <Sidebar username={username} isAdmin={isAdmin} />
      <div style={{ marginLeft: 200, flex: 1, minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        <div style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ width: '100%', maxWidth: 700, background: '#fff', borderRadius: 14, boxShadow: '0 2px 16px #0001', padding: 48 }}>
            {loading ? (
              <div style={{ fontSize: 20 }}>Yükleniyor...</div>
            ) : error ? (
              <div style={{ color: 'red', fontSize: 18 }}>{error}</div>
            ) : !product ? (
              <div style={{ fontSize: 18 }}>Ürün bulunamadı.</div>
            ) : (
              <>
                <h2 style={{ color: '#365256', marginBottom: 32, fontSize: 32 }}>{product.name}</h2>
                <div style={{ fontSize: 20, marginBottom: 12 }}><b>Açıklama:</b> {product.description || '-'}</div>
                <div style={{ fontSize: 20, marginBottom: 12 }}><b>Kategori:</b> {product.category || '-'}</div>
                <div style={{ fontSize: 20, marginBottom: 12 }}><b>Barkod:</b> {product.barcode || '-'}</div>
                <div style={{ fontSize: 20, marginBottom: 12 }}><b>Miktar:</b> {product.quantity}</div>
                <div style={{ fontSize: 20, marginBottom: 12 }}><b>Fiyat:</b> {product.price} ₺</div>
                <div style={{ fontSize: 20, marginBottom: 12 }}><b>Ekleyen:</b> {product.created_by_username || '-'}</div>
                <div style={{ fontSize: 20, marginBottom: 12 }}><b>Son Düzenleyen:</b> {product.last_edited_by_username || '-'}</div>
                <div style={{ fontSize: 20, marginBottom: 12 }}><b>Eklenme Tarihi:</b> {new Date(product.created_at).toLocaleString('tr-TR')}</div>
              </>
            )}
          </div>
        </div>
        <Footer />
      </div>
    </div>
  );
};

export default ProductDetailPage;
