import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const navigate = useNavigate();
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div style={{ maxWidth: 400, margin: '100px auto', padding: 24, boxShadow: '0 0 10px #ccc', borderRadius: 8 }}>
      <h2>Hoş Geldin!</h2>
      <button onClick={handleLogout} style={{ width: '100%', padding: 10, marginTop: 20 }}>Çıkış Yap</button>
    </div>
  );
};

export default HomePage;
