import React, { useEffect, useState } from 'react';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';
import { parseJwt } from '../utils/jwt';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './ProductDetailPage.css';

function getChangedFields(oldData, newData) {
  if (!oldData || !newData) return [];
  const changed = [];
  for (const key in newData) {
    if (key === 'id') continue;
    if (oldData[key] !== newData[key]) {
      changed.push(key);
    }
  }
  return changed;
}

const fieldLabels = {
  name: 'Ad',
  description: 'Açıklama',
  category: 'Kategori',
  barcode: 'Barkod',
  quantity: 'Miktar',
  price: 'Fiyat',
};

const ProductHistoryPage = () => {
  const { id } = useParams();
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const payload = parseJwt(token);
      setUsername(payload?.username || '');
      setIsAdmin(!!payload?.is_admin);
    }
    setLoading(true);
    setError('');
    axios.get(`http://localhost:5000/products/${id}/history`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        setHistory(res.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Geçmiş alınamadı!');
        setLoading(false);
      });
  }, [id]);

  return (
    <div style={{ minHeight: '100vh', background: '#f4f6fa', display: 'flex' }}>
      <Sidebar username={username} isAdmin={isAdmin} />
      <div style={{ marginLeft: 200, flex: 1, minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        <div style={{ flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'flex-start', paddingTop: 48 }}>
          <div className="product-detail-container" style={{ maxWidth: 1100 }}>
            <div style={{ fontSize: 28, color: '#365256', fontWeight: 700, marginBottom: 32 }}>Düzenleme Geçmişi</div>
            {loading ? (
              <div className="product-detail-loading">Yükleniyor...</div>
            ) : error ? (
              <div className="product-detail-error">{error}</div>
            ) : history.length === 0 ? (
              <div style={{ fontSize: 16, color: '#888' }}>Herhangi bir düzenleme yapılmamış.</div>
            ) : (
              <table style={{ width: '100%', fontSize: 16, borderCollapse: 'collapse' }}>
                <thead>
                  <tr style={{ background: '#f4f6fa' }}>
                    <th style={{ padding: 8 }}>Alan</th>
                    <th style={{ padding: 8 }}>Eski Değer</th>
                    <th style={{ padding: 8 }}>Yeni Değer</th>
                    <th style={{ padding: 8 }}>Düzenleyen</th>
                    <th style={{ padding: 8 }}>Tarih</th>
                  </tr>
                </thead>
                <tbody>
                  {history.map((h, i) => {
                    const changedFields = getChangedFields(h.old_data, h.new_data);
                    return changedFields.length === 0 ? null : changedFields.map(field => (
                      <tr key={h.id + '-' + field} style={{ borderBottom: '1px solid #eee' }}>
                        <td style={{ padding: 8, fontWeight: 600, color: '#365256' }}>{fieldLabels[field] || field}</td>
                        <td style={{ padding: 8, color: '#b00020', background: '#fff6f6' }}>{String(h.old_data[field] ?? '-')}</td>
                        <td style={{ padding: 8, color: '#09DF52', background: '#f6fff6', fontWeight: 600 }}>{String(h.new_data[field] ?? '-')}</td>
                        <td style={{ padding: 8 }}>{h.edited_by_username || '-'}</td>
                        <td style={{ padding: 8 }}>{new Date(h.edited_at).toLocaleString('tr-TR')}</td>
                      </tr>
                    ));
                  })}
                </tbody>
              </table>
            )}
            <button onClick={() => navigate(-1)} style={{ marginTop: 32, background: '#365256', color: '#fff', border: 'none', borderRadius: 6, padding: '10px 24px', fontWeight: 600, fontSize: 16, cursor: 'pointer' }}>Geri Dön</button>
          </div>
        </div>
        <Footer />
      </div>
    </div>
  );
};

export default ProductHistoryPage;
