import React from 'react';

const Sidebar = () => (
  <div style={{
    width: 200,
    height: '100vh',
    background: '#222',
    color: '#fff',
    position: 'fixed',
    top: 0,
    left: 0,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    paddingTop: 40,
    fontSize: 20,
    boxShadow: '2px 0 8px #0002'
  }}>
    <div style={{ marginBottom: 20, fontWeight: 'bold' }}>Ana Sayfa</div>
    {/* İleride menü eklenebilir */}
  </div>
);

export default Sidebar;
