import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import HomePage from './pages/HomePage';
import RegisterPage from './pages/RegisterPage';
import ProductListPage from './pages/ProductListPage';
import AddProductPage from './pages/AddProductPage';
import ProductDetailPage from './pages/ProductDetailPage';
import EditProductPage from './pages/EditProductPage';
import ProductHistoryPage from './pages/ProductHistoryPage';
import StockPanelPage from './pages/StockPanelPage';
import StockInPage from './pages/StockInPage';
import StockOutPage from './pages/StockOutPage';
import NotificationBell from './components/NotificationBell';
import axios from 'axios';

function App() {
  const [criticalCount, setCriticalCount] = React.useState(0);
  const [notifList, setNotifList] = React.useState([]);

  // Bildirimleri App seviyesinde çek
  React.useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get('http://localhost:5000/products/notifications', {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(res => {
          setNotifList(res.data);
          // Okunmamış kritik bildirim sayısı
          const criticalUnread = res.data.filter(n => !n.is_read && n.critical_level && n.current_quantity < n.critical_level).length;
          setCriticalCount(criticalUnread);
        })
        .catch(() => {
          setNotifList([]);
          setCriticalCount(0);
        });
    }
  }, []);

  return (
    <>
      <NotificationBell criticalCount={criticalCount} notifList={notifList} />
      <Router>
        <Routes>
          <Route path="/" element={<LoginPage />} />
          <Route path="/home" element={<HomePage />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/products" element={<ProductListPage />} />
          <Route path="/add-product" element={<AddProductPage />} />
          <Route path="/product/:id" element={<ProductDetailPage />} />
          <Route path="/edit-product/:id" element={<EditProductPage />} />
          <Route path="/product/:id/history" element={<ProductHistoryPage />} />
          <Route path="/stock-panel" element={<StockPanelPage />} />
          <Route path="/stock-in" element={<StockInPage />} />
          <Route path="/stock-out" element={<StockOutPage />} />
        </Routes>
      </Router>
    </>
  );
}

export default App;
