import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';
import { parseJwt } from '../utils/jwt';
import axios from 'axios';

const HomePage = ({ handleLogout }) => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [loginUser, setLoginUser] = useState('');
  const [loginTime, setLoginTime] = useState('');
  const [activity, setActivity] = useState([]);
  const [activityLoading, setActivityLoading] = useState(true);
  const [activityError, setActivityError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
    } else {
      const payload = parseJwt(token);
      setUsername(payload?.username || '');
      setIsAdmin(!!payload?.is_admin);
    }
    const loginUserVal = localStorage.getItem('loginUser') || '';
    setLoginUser(loginUserVal);
    setLoginTime(localStorage.getItem('loginTime') || '');
    axios.get(`http://localhost:5000/activity-log?user=${encodeURIComponent(loginUserVal)}`)
      .then(res => {
        setActivity(res.data);
        setActivityLoading(false);
      })
      .catch(() => {
        setActivityError('Son işlemler alınamadı!');
        setActivityLoading(false);
      });
  }, [navigate]);

  return (
    <div style={{ minHeight: '100vh', background: '#f4f6fa', display: 'flex' }}>
      <Sidebar username={username} isAdmin={isAdmin} onLogout={handleLogout} />
      <div style={{
        marginLeft: 200,
        flex: 1,
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
      }}>
        <div style={{ flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{
            background: '#f7f6f3',
            borderRadius: 10,
            padding: '18px 32px',
            marginBottom: 32,
            fontSize: 20,
            color: '#365256',
            fontWeight: 600,
            boxShadow: '0 2px 12px #0001',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'flex-start',
            gap: 8
          }}>
            <div>Giriş Yapan: <b>{loginUser}</b></div>
            <div>Giriş Saati: <b>{loginTime}</b></div>
          </div>
          <h2 style={{ fontSize: 32, color: '#222', marginBottom: 32 }}>Hoşgeldin{loginUser ? `, ${loginUser}` : ''}!</h2>
          {/* Son işlemler paneli */}
          <div style={{
            background: '#fff',
            borderRadius: 10,
            boxShadow: '0 2px 12px #0001',
            padding: '18px 32px',
            marginBottom: 32,
            minWidth: 380,
            maxWidth: 480,
            fontSize: 16,
            color: '#365256',
            fontWeight: 500,
            display: 'flex',
            flexDirection: 'column',
            gap: 10
          }}>
            <div style={{ fontWeight: 700, fontSize: 18, marginBottom: 8 }}>Senin Son İşlemlerin</div>
            {activityLoading ? (
              <div>Yükleniyor...</div>
            ) : activityError ? (
              <div style={{ color: '#b00020' }}>{activityError}</div>
            ) : activity.length === 0 ? (
              <div>Henüz işlem yok.</div>
            ) : (
              activity.map(a => (
                <div key={a.id} style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                  <span style={{ fontSize: 18 }}>
                    {a.type === 'add' && '✔'}
                    {a.type === 'update' && '✏️'}
                    {a.type === 'stock_in' && '➕'}
                    {a.type === 'stock_out' && '➖'}
                    {a.type === 'delete' && '🗑️'}
                  </span>
                  <span style={{ minWidth: 60, color: '#888', fontSize: 15 }}>{new Date(a.created_at).toLocaleTimeString('tr-TR', { hour: '2-digit', minute: '2-digit' })}</span>
                  <span>{a.message}</span>
                </div>
              ))
            )}
          </div>
        </div>
        <Footer />
      </div>
    </div>
  );
};

export default HomePage;
