import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';

const HomePage = () => {
  const navigate = useNavigate();
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div style={{ minHeight: '100vh', background: '#f4f6fa' }}>
      <Sidebar />
      <div style={{ marginLeft: 200, padding: '60px 24px 80px 24px', minHeight: '100vh', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
        <h2 style={{ fontSize: 32, color: '#222', marginBottom: 32 }}>Hoşgeldin!</h2>
        <button onClick={handleLogout} style={{ padding: '10px 32px', fontSize: 18, background: '#222', color: '#fff', border: 'none', borderRadius: 6, cursor: 'pointer' }}>Çıkış Yap</button>
      </div>
      <Footer />
    </div>
  );
};

export default HomePage;
