import React from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import HomePage from './pages/HomePage';
import RegisterPage from './pages/RegisterPage';
import ProductListPage from './pages/ProductListPage';
import AddProductPage from './pages/AddProductPage';
import ProductDetailPage from './pages/ProductDetailPage';
import EditProductPage from './pages/EditProductPage';
import ProductHistoryPage from './pages/ProductHistoryPage';
import StockPanelPage from './pages/StockPanelPage';
import StockInPage from './pages/StockInPage';
import StockOutPage from './pages/StockOutPage';
import NotificationBell from './components/NotificationBell';
import axios from 'axios';
import { NotificationProvider } from './context/NotificationContext';

function AppRoutes() {
  const navigate = useNavigate();
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };
  return (
    <Routes>
      <Route path="/" element={<LoginPage />} />
      <Route path="/home" element={<HomePage onLogout={handleLogout} />} />
      <Route path="/register" element={<RegisterPage onLogout={handleLogout} />} />
      <Route path="/products" element={<ProductListPage onLogout={handleLogout} />} />
      <Route path="/add-product" element={<AddProductPage onLogout={handleLogout} />} />
      <Route path="/product/:id" element={<ProductDetailPage onLogout={handleLogout} />} />
      <Route path="/edit-product/:id" element={<EditProductPage onLogout={handleLogout} />} />
      <Route path="/product/:id/history" element={<ProductHistoryPage onLogout={handleLogout} />} />
      <Route path="/stock-panel" element={<StockPanelPage onLogout={handleLogout} />} />
      <Route path="/stock-in" element={<StockInPage onLogout={handleLogout} />} />
      <Route path="/stock-out" element={<StockOutPage onLogout={handleLogout} />} />
    </Routes>
  );
}

function App() {
  return (
    <NotificationProvider>
      <NotificationBell />
      <Router>
        <AppRoutes />
      </Router>
    </NotificationProvider>
  );
}

export default App;
