import React, { useState, useEffect } from 'react';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';
import { parseJwt } from '../utils/jwt';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ProductListPage = () => {
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const payload = parseJwt(token);
      setUsername(payload?.username || '');
      setIsAdmin(!!payload?.is_admin);
    }
  }, []);

  useEffect(() => {
    setLoading(true);
    axios.get('http://localhost:5000/products')
      .then(res => {
        setProducts(res.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Ürünler alınamadı!');
        setLoading(false);
      });
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Bu ürünü silmek istediğinize emin misiniz?')) return;
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProducts(products.filter(p => p.id !== id));
    } catch {
      alert('Ürün silinemedi!');
    }
  };

  const handleEdit = (id) => {
    navigate(`/edit-product/${id}`);
  };

  return (
    <div style={{ minHeight: '100vh', background: '#f4f6fa', display: 'flex' }}>
      <Sidebar username={username} isAdmin={isAdmin} />
      <div style={{ marginLeft: 200, flex: 1, minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        <div style={{ flex: 1, display: 'flex', alignItems: 'flex-start', justifyContent: 'center', paddingTop: 60 }}>
          <div style={{ width: '100%', maxWidth: 700, background: '#fff', borderRadius: 10, boxShadow: '0 2px 12px #0001', padding: 32 }}>
            <h2 style={{ color: '#365256', marginBottom: 24 }}>Ürün Listesi</h2>
            {loading ? (
              <div>Yükleniyor...</div>
            ) : error ? (
              <div style={{ color: 'red' }}>{error}</div>
            ) : products.length === 0 ? (
              <div>Hiç ürün yok.</div>
            ) : (
              <table style={{ width: '100%', borderCollapse: 'collapse', fontSize: 17 }}>
                <thead>
                  <tr style={{ background: '#f7f6f3' }}>
                    <th style={{ padding: 8, textAlign: 'left', color: '#365256' }}>Ad</th>
                    <th style={{ padding: 8, textAlign: 'left', color: '#365256' }}>Miktar</th>
                    <th style={{ padding: 8, textAlign: 'left', color: '#365256' }}>Fiyat</th>
                    <th style={{ padding: 8, textAlign: 'left', color: '#365256' }}>Eklenme Tarihi</th>
                    <th style={{ padding: 8, textAlign: 'left', color: '#365256' }}>Ekleyen Kullanıcı</th>
                    <th style={{ padding: 8, textAlign: 'left', color: '#365256' }}>İşlemler</th>
                  </tr>
                </thead>
                <tbody>
                  {products.map(p => (
                    <tr key={p.id} style={{ borderBottom: '1px solid #eee' }}>
                      <td style={{ padding: 8 }}>{p.name}</td>
                      <td style={{ padding: 8 }}>{p.quantity}</td>
                      <td style={{ padding: 8 }}>{p.price} ₺</td>
                      <td style={{ padding: 8 }}>{new Date(p.created_at).toLocaleString('tr-TR')}</td>
                      <td style={{ padding: 8 }}>{p.created_by_username || '-'}</td>
                      <td style={{ padding: 8 }}>
                        <div style={{ display: 'flex', gap: 8 }}>
                          <button onClick={() => handleEdit(p.id)} style={{ padding: '4px 10px', background: '#E8A652', color: '#222', border: 'none', borderRadius: 4, cursor: 'pointer' }}>Düzenle</button>
                          <button onClick={() => handleDelete(p.id)} style={{ padding: '4px 10px', background: '#b00020', color: '#fff', border: 'none', borderRadius: 4, cursor: 'pointer' }}>Sil</button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
        <Footer />
      </div>
    </div>
  );
};

export default ProductListPage;
