import React from 'react';
import './Sidebar.css';
import { NavLink } from 'react-router-dom';
import { useState } from 'react';

const Sidebar = ({ username, isAdmin, onLogout }) => {
  const [showCriticalModal, setShowCriticalModal] = useState(false);
  // Kritik stokta ürün sayısı örnek (backend entegrasyonu sonra)
  const criticalCount = 0; // TODO: backend'den çekilecek

  const handleLogoutClick = () => {
    if (window.confirm('Gerçekten çıkış yapmak istiyor musunuz?')) {
      onLogout && onLogout();
    }
  };

  return (
    <div className="sidebar">
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: 12 }}>
        <div className="sidebar-title">{username ? username : 'Kullanıcı'}</div>
        <div style={{ position: 'relative', cursor: 'pointer' }} onClick={() => setShowCriticalModal(true)}>
          <span style={{ fontSize: 22, color: criticalCount > 0 ? '#b00020' : '#888' }} role="img" aria-label="Kritik Stok Bildirimi">🔔</span>
          {criticalCount > 0 && (
            <span style={{ position: 'absolute', top: -6, right: -8, background: '#b00020', color: '#fff', borderRadius: '50%', fontSize: 13, fontWeight: 700, padding: '2px 7px', minWidth: 22, textAlign: 'center' }}>{criticalCount}</span>
          )}
        </div>
      </div>
      {/* Modal örneği, detay sonra */}
      {showCriticalModal && (
        <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.18)', zIndex: 3000, display: 'flex', alignItems: 'center', justifyContent: 'center' }} onClick={() => setShowCriticalModal(false)}>
          <div style={{ background: '#fff', borderRadius: 14, boxShadow: '0 2px 24px #0002', padding: 36, minWidth: 320, maxWidth: '90vw', maxHeight: '80vh', overflowY: 'auto', position: 'relative' }} onClick={e => e.stopPropagation()}>
            <div style={{ fontSize: 20, color: '#b00020', fontWeight: 700, marginBottom: 18 }}>Kritik Stokta Ürünler</div>
            <div style={{ color: '#888', fontSize: 16 }}>Burada kritik stokta ürünler listelenecek.</div>
            <button style={{ marginTop: 24, background: '#365256', color: '#fff', border: 'none', borderRadius: 6, padding: '10px 24px', fontWeight: 600, fontSize: 16, cursor: 'pointer' }} onClick={() => setShowCriticalModal(false)}>Kapat</button>
          </div>
        </div>
      )}
      <nav className="sidebar-menu">
        <NavLink to="/home" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'} >Ana Sayfa</NavLink>
        <NavLink to="/products" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Ürün Listesi</NavLink>
        <NavLink to="/add-product" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Ürün Ekle</NavLink>
        <NavLink to="/stock-panel" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Stok Durum Paneli</NavLink>
        <NavLink to="/stock-in" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Stok Girişi</NavLink>
        <NavLink to="/stock-out" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Stok Çıkışı</NavLink>
        {isAdmin && (
          <NavLink to="/register" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Üye Kaydı</NavLink>
        )}
      </nav>
      <button
        className="sidebar-logout"
        onClick={handleLogoutClick}
      >
        Çıkış Yap
      </button>
    </div>
  );
};

export default Sidebar;
