import React from 'react';
import './Sidebar.css';
import { NavLink } from 'react-router-dom';

const Sidebar = ({ username, isAdmin, onLogout, notifCount = 0, notifList = [], onNotifClick, notifPanelOpen }) => {
  const handleLogoutClick = () => {
    if (window.confirm('Gerçekten çıkış yapmak istiyor musunuz?')) {
      onLogout && onLogout();
    }
  };

  return (
    <div className="sidebar">
      {/* Bildirim ikonu */}
      <div style={{ position: 'relative', marginBottom: 18, display: 'flex', justifyContent: 'center' }}>
        <div style={{ position: 'relative', cursor: 'pointer' }} onClick={onNotifClick}>
          <svg width="28" height="28" viewBox="0 0 24 24" fill="none" stroke="#365256" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M18 8a6 6 0 0 0-12 0c0 7-3 9-3 9h18s-3-2-3-9"/><path d="M13.73 21a2 2 0 0 1-3.46 0"/></svg>
          {notifCount > 0 && (
            <span style={{ position: 'absolute', top: -6, right: -6, background: '#b00020', color: '#fff', borderRadius: '50%', fontSize: 12, fontWeight: 700, width: 18, height: 18, display: 'flex', alignItems: 'center', justifyContent: 'center', boxShadow: '0 2px 8px #0002' }}>{notifCount}</span>
          )}
        </div>
        {/* Bildirim paneli (ikon tıklanınca) */}
        {notifPanelOpen && notifList.length > 0 && (
          <div style={{ position: 'absolute', top: 32, right: 0, background: '#fff', borderRadius: 8, boxShadow: '0 2px 12px #0002', padding: '12px 18px', minWidth: 180, color: '#b00020', fontWeight: 600, fontSize: 14, zIndex: 3000 }}>
            {notifList.map(n => <div key={n.id}>{n.message}</div>)}
          </div>
        )}
      </div>
      <div className="sidebar-title">{username ? username : 'Kullanıcı'}</div>
      <nav className="sidebar-menu">
        <NavLink to="/home" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'} >Ana Sayfa</NavLink>
        <NavLink to="/products" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Ürün Listesi</NavLink>
        <NavLink to="/add-product" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Ürün Ekle</NavLink>
        <NavLink to="/stock-panel" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Stok Durum Paneli</NavLink>
        <NavLink to="/stock-in" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Stok Girişi</NavLink>
        <NavLink to="/stock-out" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Stok Çıkışı</NavLink>
        {isAdmin && (
          <NavLink to="/register" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Üye Kaydı</NavLink>
        )}
      </nav>
      <button
        className="sidebar-logout"
        onClick={handleLogoutClick}
      >
        Çıkış Yap
      </button>
    </div>
  );
};

export default Sidebar;
