import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';
import { parseJwt } from '../utils/jwt';
import axios from 'axios';
import './ProductDetailPage.css';

function getChangedFields(oldData, newData) {
  if (!oldData || !newData) return [];
  const changed = [];
  for (const key in newData) {
    if (key === 'id') continue;
    if (oldData[key] !== newData[key]) {
      changed.push(key);
    }
  }
  return changed;
}

function getLastChangeSummary(history) {
  if (!history || history.length === 0) return null;
  const last = history[0];
  const changedFields = getChangedFields(last.old_data, last.new_data);
  return {
    user: last.edited_by_username || '-',
    date: new Date(last.edited_at).toLocaleString('tr-TR'),
    fields: changedFields
  };
}

const ProductDetailPage = () => {
  const { id } = useParams();
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [history, setHistory] = useState([]);
  const [historyLoading, setHistoryLoading] = useState(true);
  const [historyError, setHistoryError] = useState('');
  const [showHistoryModal, setShowHistoryModal] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const payload = parseJwt(token);
      setUsername(payload?.username || '');
      setIsAdmin(!!payload?.is_admin);
    }
  }, []);

  useEffect(() => {
    setLoading(true);
    axios.get(`http://localhost:5000/products`)
      .then(res => {
        const found = res.data.find(p => String(p.id) === String(id));
        setProduct(found);
        setLoading(false);
      })
      .catch(() => {
        setError('Ürün bilgisi alınamadı!');
        setLoading(false);
      });
    // Geçmişi çek
    setHistoryLoading(true);
    setHistoryError('');
    const token = localStorage.getItem('token');
    axios.get(`http://localhost:5000/products/${id}/history`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        setHistory(res.data);
        setHistoryLoading(false);
      })
      .catch(() => {
        setHistoryError('Geçmiş alınamadı!');
        setHistoryLoading(false);
      });
  }, [id]);

  const lastChange = getLastChangeSummary(history);

  return (
    <div style={{ minHeight: '100vh', background: '#f4f6fa', display: 'flex' }}>
      <Sidebar username={username} isAdmin={isAdmin} />
      <div style={{ marginLeft: 200, flex: 1, minHeight: '100vh', display: 'flex', flexDirection: 'column', position: 'relative' }}>
        <div style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div className="product-detail-container">
            {/* Son değişiklik özeti */}
            {lastChange && (
              <div style={{
                background: '#f7f6f3',
                borderRadius: 8,
                padding: '12px 18px',
                marginBottom: 18,
                fontSize: 16,
                color: '#365256',
                display: 'flex',
                alignItems: 'center',
                gap: 10
              }}>
                <b>Son değişiklik:</b>
                {lastChange.fields.length === 0 ? (
                  <span>Alan değişikliği yok</span>
                ) : (
                  lastChange.fields.map(f => (
                    <span key={f} style={{ background: '#E8A652', color: '#222', borderRadius: 4, padding: '2px 8px', marginRight: 6, fontWeight: 500 }}>{f}</span>
                  ))
                )}
                <span style={{ color: '#888', marginLeft: 8 }}>
                  {lastChange.date} - {lastChange.user}
                </span>
              </div>
            )}
            {loading ? (
              <div className="product-detail-loading">Yükleniyor...</div>
            ) : error ? (
              <div className="product-detail-error">{error}</div>
            ) : !product ? (
              <div className="product-detail-error">Ürün bulunamadı.</div>
            ) : (
              <>
                <div className="product-detail-title">{product.name}</div>
                <div className="product-detail-info"><b>Açıklama:</b> {product.description || '-'}</div>
                <div className="product-detail-info"><b>Kategori:</b> {product.category || '-'}</div>
                <div className="product-detail-info"><b>Barkod:</b> {product.barcode || '-'}</div>
                <div className="product-detail-info"><b>Miktar:</b> {product.quantity}</div>
                <div className="product-detail-info"><b>Fiyat:</b> {product.price} ₺</div>
                <div className="product-detail-info"><b>Ekleyen:</b> {product.created_by_username || '-'}</div>
                <div className="product-detail-info"><b>Son Düzenleyen:</b> {product.last_edited_by_username || '-'}</div>
                <div className="product-detail-info"><b>Eklenme Tarihi:</b> {new Date(product.created_at).toLocaleString('tr-TR')}</div>
                {/* Geçmiş Tablosu */}
                <div style={{ marginTop: 40 }}>
                  <h3 style={{ color: '#365256', fontSize: 22, marginBottom: 16 }}>Düzenleme Geçmişi</h3>
                  {historyLoading ? (
                    <div className="product-detail-loading">Geçmiş yükleniyor...</div>
                  ) : historyError ? (
                    <div className="product-detail-error">{historyError}</div>
                  ) : history.length === 0 ? (
                    <div style={{ fontSize: 16, color: '#888' }}>Herhangi bir düzenleme yapılmamış.</div>
                  ) : (
                    <table style={{ width: '100%', fontSize: 16, borderCollapse: 'collapse' }}>
                      <thead>
                        <tr style={{ background: '#f4f6fa' }}>
                          <th style={{ padding: 8, textAlign: 'left' }}>Düzenleyen</th>
                          <th style={{ padding: 8, textAlign: 'left' }}>Tarih</th>
                          <th style={{ padding: 8, textAlign: 'left' }}>Değişen Alanlar</th>
                        </tr>
                      </thead>
                      <tbody>
                        {history.map((h, i) => {
                          const changedFields = getChangedFields(h.old_data, h.new_data);
                          return (
                            <tr key={h.id} style={{ borderBottom: '1px solid #eee' }}>
                              <td style={{ padding: 8 }}>{h.edited_by_username || '-'}</td>
                              <td style={{ padding: 8 }}>{new Date(h.edited_at).toLocaleString('tr-TR')}</td>
                              <td style={{ padding: 8 }}>
                                {changedFields.length === 0 ? (
                                  <span style={{ color: '#888' }}>Alan değişikliği yok</span>
                                ) : (
                                  changedFields.map(f => (
                                    <span key={f} style={{ background: '#E8A652', color: '#222', borderRadius: 4, padding: '2px 8px', marginRight: 6, fontWeight: 500 }}>{f}</span>
                                  ))
                                )}
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  )}
                </div>
              </>
            )}
            {/* Modal */}
            {showHistoryModal && (
              <div className="product-history-modal-bg" onClick={() => setShowHistoryModal(false)}>
                <div className="product-history-modal" onClick={e => e.stopPropagation()}>
                  <h3 style={{ color: '#365256', fontSize: 22, marginBottom: 16 }}>Düzenleme Geçmişi</h3>
                  {historyLoading ? (
                    <div className="product-detail-loading">Geçmiş yükleniyor...</div>
                  ) : historyError ? (
                    <div className="product-detail-error">{historyError}</div>
                  ) : history.length === 0 ? (
                    <div style={{ fontSize: 16, color: '#888' }}>Herhangi bir düzenleme yapılmamış.</div>
                  ) : (
                    <table style={{ width: '100%', fontSize: 16, borderCollapse: 'collapse' }}>
                      <thead>
                        <tr style={{ background: '#f4f6fa' }}>
                          <th style={{ padding: 8, textAlign: 'left' }}>Düzenleyen</th>
                          <th style={{ padding: 8, textAlign: 'left' }}>Tarih</th>
                          <th style={{ padding: 8, textAlign: 'left' }}>Değişen Alanlar</th>
                        </tr>
                      </thead>
                      <tbody>
                        {history.map((h, i) => {
                          const changedFields = getChangedFields(h.old_data, h.new_data);
                          return (
                            <tr key={h.id} style={{ borderBottom: '1px solid #eee' }}>
                              <td style={{ padding: 8 }}>{h.edited_by_username || '-'}</td>
                              <td style={{ padding: 8 }}>{new Date(h.edited_at).toLocaleString('tr-TR')}</td>
                              <td style={{ padding: 8 }}>
                                {changedFields.length === 0 ? (
                                  <span style={{ color: '#888' }}>Alan değişikliği yok</span>
                                ) : (
                                  changedFields.map(f => (
                                    <span key={f} style={{ background: '#E8A652', color: '#222', borderRadius: 4, padding: '2px 8px', marginRight: 6, fontWeight: 500 }}>{f}</span>
                                  ))
                                )}
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  )}
                  <button style={{ marginTop: 24, background: '#365256', color: '#fff', border: 'none', borderRadius: 6, padding: '10px 24px', fontWeight: 600, fontSize: 16, cursor: 'pointer' }} onClick={() => setShowHistoryModal(false)}>Kapat</button>
                </div>
              </div>
            )}
            {/* Sağ alt köşe butonu */}
            <button
              className="product-history-fab"
              onClick={() => navigate(`/product/${id}/history`)}
              style={{ position: 'fixed', right: 36, bottom: 36, zIndex: 1000, background: '#E8A652', color: '#222', border: 'none', borderRadius: '50px', padding: '16px 28px', fontWeight: 600, fontSize: 17, boxShadow: '0 2px 12px #0002', cursor: 'pointer' }}
            >
              Düzenleme Geçmişini Görüntüle
            </button>
          </div>
        </div>
        <Footer />
      </div>
    </div>
  );
};

export default ProductDetailPage;
