import React from 'react';
import './Sidebar.css';
import { NavLink } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';

const Sidebar = ({ username, isAdmin, onLogout }) => {
  const [showCriticalModal, setShowCriticalModal] = useState(false);
  const [criticalProducts, setCriticalProducts] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get('http://localhost:5000/products/critical-stock', {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(res => setCriticalProducts(res.data))
        .catch(() => setCriticalProducts([]));
    }
  }, []);
  const criticalCount = criticalProducts.length;

  const handleLogoutClick = () => {
    if (window.confirm('GerÃ§ekten Ã§Ä±kÄ±ÅŸ yapmak istiyor musunuz?')) {
      onLogout && onLogout();
    }
  };

  return (
    <div className="sidebar">
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginBottom: 12 }}>
        <div className="sidebar-title">{username ? username : 'KullanÄ±cÄ±'}</div>
        <div style={{ position: 'relative', cursor: 'pointer', marginTop: 8 }} onClick={() => setShowCriticalModal(true)}>
          <span style={{ fontSize: 22, color: criticalCount > 0 ? '#b00020' : '#888' }} role="img" aria-label="Kritik Stok Bildirimi">ðŸ””</span>
          {criticalCount > 0 && (
            <span style={{ position: 'absolute', top: -6, right: -8, background: '#b00020', color: '#fff', borderRadius: '50%', fontSize: 13, fontWeight: 700, padding: '2px 7px', minWidth: 22, textAlign: 'center' }}>{criticalCount}</span>
          )}
        </div>
      </div>
      {/* Modal Ã¶rneÄŸi, detay sonra */}
      {showCriticalModal && (
        <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.18)', zIndex: 3000, display: 'flex', alignItems: 'center', justifyContent: 'center' }} onClick={() => setShowCriticalModal(false)}>
          <div style={{ background: '#fff', borderRadius: 14, boxShadow: '0 2px 24px #0002', padding: 36, minWidth: 320, maxWidth: '90vw', maxHeight: '80vh', overflowY: 'auto', position: 'relative' }} onClick={e => e.stopPropagation()}>
            <div style={{ fontSize: 20, color: '#b00020', fontWeight: 700, marginBottom: 18 }}>Kritik Stokta ÃœrÃ¼nler</div>
            {criticalProducts.length === 0 ? (
              <div style={{ color: '#888', fontSize: 16 }}>Kritik stokta Ã¼rÃ¼n yok.</div>
            ) : (
              <table style={{ width: '100%', fontSize: 15, borderCollapse: 'collapse', marginBottom: 16 }}>
                <thead>
                  <tr style={{ background: '#f4f6fa' }}>
                    <th style={{ padding: 8, textAlign: 'left' }}>Ad</th>
                    <th style={{ padding: 8, textAlign: 'left' }}>Miktar</th>
                    <th style={{ padding: 8, textAlign: 'left' }}>Kritik Seviye</th>
                    <th style={{ padding: 8, textAlign: 'left' }}>Kategori</th>
                    <th style={{ padding: 8, textAlign: 'left' }}>Barkod</th>
                  </tr>
                </thead>
                <tbody>
                  {criticalProducts.map(p => (
                    <tr key={p.id} style={{ borderBottom: '1px solid #eee' }}>
                      <td style={{ padding: 8 }}>{p.name}</td>
                      <td style={{ padding: 8, color: '#b00020', fontWeight: 600 }}>{p.quantity}</td>
                      <td style={{ padding: 8 }}>{p.critical_stock}</td>
                      <td style={{ padding: 8 }}>{p.category || '-'}</td>
                      <td style={{ padding: 8 }}>{p.barcode || '-'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
            <button style={{ marginTop: 24, background: '#365256', color: '#fff', border: 'none', borderRadius: 6, padding: '10px 24px', fontWeight: 600, fontSize: 16, cursor: 'pointer' }} onClick={() => setShowCriticalModal(false)}>Kapat</button>
          </div>
        </div>
      )}
      <nav className="sidebar-menu">
        <NavLink to="/home" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'} >Ana Sayfa</NavLink>
        <NavLink to="/products" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>ÃœrÃ¼n Listesi</NavLink>
        <NavLink to="/add-product" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>ÃœrÃ¼n Ekle</NavLink>
        <NavLink to="/stock-panel" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Stok Durum Paneli</NavLink>
        <NavLink to="/stock-in" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Stok GiriÅŸi</NavLink>
        <NavLink to="/stock-out" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Stok Ã‡Ä±kÄ±ÅŸÄ±</NavLink>
        {isAdmin && (
          <NavLink to="/register" className={({ isActive }) => isActive ? 'sidebar-link active' : 'sidebar-link'}>Ãœye KaydÄ±</NavLink>
        )}
      </nav>
      <button
        className="sidebar-logout"
        onClick={handleLogoutClick}
      >
        Ã‡Ä±kÄ±ÅŸ Yap
      </button>
    </div>
  );
};

export default Sidebar;
